const gulp = require('gulp');
const uglify = require('gulp-uglify');
const babel = require('gulp-babel');

gulp.task('default', ['js', 'css']);

gulp.task('js', function () {
  return gulp.src('src/opentok-solutions-logging.js')
    .pipe(babel({ presets: ['es2015-script'], plugins: ['transform-object-assign'] }))
    .pipe(uglify())
    .pipe(gulp.dest('dist'));
});

gulp.task('dist', ['js']);
